import React, { useState } from 'react';
import axios from 'axios';
import './ProductTile.css';

export default function ProductTile({ product_id, image, description, price, onAddToBasket }) {
  const [selectedQty, setSelectedQty] = useState(1);
  const [error, setError] = useState('');

  const handleAddClick = async () => {
    try {
      // 🔍 Fetch latest quantity from backend
      const response = await axios.get(`http://localhost:5002/api/products/${product_id}`);
      const latestProduct = response.data;

      if (selectedQty > latestProduct.quantity) {
        setError('Out of Stock');
      } else {
        setError('');
        onAddToBasket({
          product_id,
          image,
          description,
          price,
          quantity: selectedQty
        });
      }
    } catch (err) {
      console.error('Error checking stock:', err);
      setError('Error checking stock');
    }
  };

  return (
    <div className="product-tile">
      <img
        src={image}
        alt="Product"
        className="product-image"
        style={{ width: "10rem", height: "10rem", marginTop: "20px" }}
      />
      <div className="product-info">
        <p className="product-description">{description}</p>
        <p className="product-price">Rs {price}/-</p>

        <div>
          <input
            type="number"
            min="1"
            value={selectedQty}
            onChange={(e) => setSelectedQty(Number(e.target.value))}
            style={{ width: '60px', marginRight: '5px' }}
          />
          <button onClick={handleAddClick}>Add</button>
          {error && <p style={{ color: 'red', fontSize: '12px' }}>{error}</p>}
        </div>
      </div>
    </div>
  );
}
